import axios from "axios"
import { useEffect, useReducer } from "react"

let DataFetchTwo = () => {
    let initialState = {
        loading : true,
        data : {},
        error : ''
    }
    let reducer = (state, action) => {
        switch(action.type) {
            case 'fetch-success' : 
                return {
                    loading : false,
                    data : action.data,
                    error : ''
                }
            case 'fetch-error' :
                return {
                    loading : false,
                    data : {},
                    error : action.data
                }
            default : 
                return state
        }
    }
    useEffect(() => {
        axios.get("https://jsonplaceholder.typicode.com/posts/1")
        .then(res => {
            dispatch({type : 'fetch-success', data : res.data})
        })
        .catch(err => {
            dispatch({type : 'fetch-error', data : err})
        })
    }, [])

    let [state, dispatch] = useReducer(reducer, initialState)

    return (
        <>
            <div>{ state.loading ? 'Waiting ... ' : state.data }</div>
            <br />
            <div>{ state.error ? error : "No Error" }</div>
        </>
    )
}

export default DataFetchTwo
